<?phpnamespace Bfg\Layout\Core;use Livewire\Component;use Livewire\LivewireComponentsFinder;use Symfony\Component\Finder\SplFileInfo;use function Livewire\str;/** * Class LayoutLivewireComponentsFinder * @package Bfg\Layout\Core */class LayoutLivewireComponentsFinder extends LivewireComponentsFinder{    /**     * @var array     */    protected static array $paths = [];    /**     * @param  string  $namespace     * @param  string  $path     */    public static function directory(string $namespace, string $path)    {        static::$paths[$namespace] = $path;    }    /**     * @return \Illuminate\Support\Collection     */    public function getClassNames()    {        $result = parent::getClassNames();        foreach (static::$paths as $namespace => $path) {            $result_to_merge = collect($this->files->allFiles($path))                ->map(function (SplFileInfo $file) use ($namespace, $path) {                    return $namespace . "\\" . str($file->getPathname())                            ->after($path.'/')                            ->replace(['/', '.php'], ['\\', ''])->__toString();                })                ->filter(function (string $class) {                    return is_subclass_of($class, Component::class) &&                        ! (new \ReflectionClass($class))->isAbstract();                });            $result = $result->merge($result_to_merge);        }        return $result;    }}